// print("------- \(ckf) \(fkf)") return ckf > fkf ? fk : ck
} fileprivate func falsePositiveProbability(m: Int, n: Int, k: Int) -> Double { let _k = Double(k) let _n = Double(n) let _m = Double(m) let f = pow(1.0 - exp(-_k * _n / _m), _k) return f
} open class BloomFilter { private var bits: [Bool] private let hashCount: Int private let seed: UInt32 // m/n 最优比例（平衡误报率和内存占用）9.8385e-6 < 1e-5 public static let mnRate1e_5 = 24 /// init function /// - Parameters: /// - maxElements: maximum capacity /// - seed: hash seed /// - rate: rate of bit to maximum capacity public init(maxElements: Int, seed: UInt32 = 0, rate: Int = mnRate1e_5) { let n = maxElements let m = n * rate let k = optimalK(m: m, n: n)
// print("optimal K \(k)") self.bits = Array(repeating: false, count: m) self.hashCount = k self.seed = seed } /// add to bloom filter /// - Parameter string: value open func add(_ string: String) { let hashes = self.hashValues(string) for hash in hashes { self.bits[hash] = true } } /// if the value is in the bloom filter /// - Parameter string: value /// - Returns: result open func contains(_ string: String) -> Bool { let hashes = self.hashValues(string) for hash in hashes { if !self.bits[hash] { return false } } return true } private func hashValues(_ string: String) -> [Int] { var hashValues = [Int]() let data = string.data(using: .utf8)! for i in 0..<self.hashCount { let hash = MurmurHash3.sum32(data, seed: self.seed &+ UInt32(i)) let index = abs(Int(hash)) % self.bits.count hashValues.append(index) } return hashValues }
}

